--Selecting service centers by city name
SELECT s.id, s.title, s.address, sc.title as city, b.title FROM service_centers_uk as s 
left join service_centers_cities_uk sc 
ON s.id_service_centers_cities=sc.id 
left join service_centers_brands as sb ON sb.id_service_centers=s.id
left join brands_uk as b on sb.id_brands=b.id
where sc.title="Бахмут";

--Selecting service centers by BRAND ID (107 - is Neoclima)
SELECT s.id, s.title, s.address, sc.title as city, b.title FROM service_centers_uk as s 
left join service_centers_cities_uk sc 
ON s.id_service_centers_cities=sc.id 
left join service_centers_brands as sb ON sb.id_service_centers=s.id
left join brands_uk as b on sb.id_brands=b.id 
where b.id=107;

